import React, {useState, createContext, useContext} from "react"
import {useLocation} from "react-router-dom"

const ResultContext = createContext()

export const ResultContextProvider = ({children}) => {
  const [result, setResult] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [searchTerm, setSearchTerm] = useState("")
  const [baseUrl, setBaseUrl] = useState("")
  const [options, setOptions] = useState({})

  const getResult = async (type) => {
    const location = useLocation()
    switch (location.pathname) {
      case "/search":
        setBaseUrl(
          `https://bing-web-search1.p.rapidapi.com/search?q=${type}&freshness=Day&textFormat=Raw&safeSearch=Off&mkt=en-us`
        )

        setOptions({
          method: "GET",
          headers: {
            "X-BingApis-SDK": "true",
            "X-RapidAPI-Key": "723b0ce433msh85db6a452b6f6ebp111cf7jsn69ab9b7fe19b",
            "X-RapidAPI-Host": "bing-web-search1.p.rapidapi.com",
          },
        })

        break

      default:
        break
    }
    setIsLoading(true)

    const imageUrl = "https://bing-image-search1.p.rapidapi.com/images/search?q=ham"
    const options = {
      method: "GET",
      headers: {
        "X-RapidAPI-Key": "723b0ce433msh85db6a452b6f6ebp111cf7jsn69ab9b7fe19b",
        "X-RapidAPI-Host": "bing-image-search1.p.rapidapi.com",
      },
    }
    const response = await fetch(baseUrl, options)

    const data = await response.json()
    console.log(data)

    setResult(data)
    setIsLoading(false)
  }

  return (
    <ResultContext.Provider value={{getResult, result, searchTerm, setSearchTerm, isLoading}}>
      {children}
    </ResultContext.Provider>
  )
}

export const useResultContext = () => {
  return useContext(ResultContext)
}
