import React, {useEffect} from "react"
import {useLocation} from "react-router-dom"
import {ReactPlayer} from "react-player"
import {useResultContext} from "../contexts/ResultContextProvider"
import {Loading} from "./Loading"

export const Results = () => {
  const {result, getResult, searchTerm, isLoading} = useResultContext()
  const location = useLocation()

  useEffect(() => {
    getResult("javascript")
  }, [])

  if (isLoading) return <Loading />

  switch (location.pathname) {
    case "/search":
      return (
        <div className="flex flex-wrap justify-between space-y-6 sm:px-56">
          {result?.results?.map(({url, title}, index) => (
            <div key={index} className="w-full md:w:2/5">
              <a href={url} target="_blank" rel="noreferrer">
                <p className="text-sm">{url.length > 30 ? url.substring(0, 30) : url}</p>

                <p className="text-lg hover:underline dark:text-blue-300 text-blue-700">{title}</p>
                <p></p>
              </a>
            </div>
          ))}
        </div>
      )
      break
    case "/images":
      return (
        <div className="flex flex-wrap justify-center items-center ">
          {result?.image_results?.map()}
        </div>
      )
      break
    case "/videos":
      return "searching"
      break
    case "/news":
      return "searching"
      break

    default:
      return "ERROR!"
      break
  }
}
