import React, {useState, createContext, useContext} from "react"

const ResultContext = createContext()
const baseUrl = "https://google-web-search1.p.rapidapi.com/"

const ResultContextProvider = ({children}) => {
  const [result, setResult] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [searchTerm, setSearchTerm] = useState("")

  const getResult = async (type) => {
    setIsLoading(true)

    const response = await fetch(`${baseUrl}${type}`, {
      method: "GET",
      headers: {
        "X-RapidAPI-Key": "723b0ce433msh85db6a452b6f6ebp111cf7jsn69ab9b7fe19b",
        "X-RapidAPI-Host": "google-web-search1.p.rapidapi.com",
      },
    })
    const data = await response.json()

    setResult(data)
    setIsLoading(false)
  }

  return (
    <ResultContext.Provider value={{getResult, result, searchTerm, setSearchTerm, isLoading}}>
      {children}
    </ResultContext.Provider>
  )
}

const useResultContext = () => useContext(ResultContext)

export default {useResultContext, ResultContextProvider}
