import React, {useState, createContext, useContext} from "react"

const ResultContext = createContext()

export const ResultContextProvider = ({children}) => {
  const [result, setResult] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [searchTerm, setSearchTerm] = useState("")
  const [baseUrl, setBaseUrl] = useState("")
  const [options, setOptions] = useState({})
  const base = (i) => {
    return {
      images: () => {
        setBaseUrl(`https://bing-image-search1.p.rapidapi.com/images/search?q=${i}`)
        setOptions({
          method: "GET",
          headers: {
            "X-RapidAPI-Key": "723b0ce433msh85db6a452b6f6ebp111cf7jsn69ab9b7fe19b",
            "X-RapidAPI-Host": "bing-image-search1.p.rapidapi.com",
          },
        })
      },
      link: () => {
        setBaseUrl(
          `https://bing-web-search1.p.rapidapi.com/search?q=${i}&freshness=Day&textFormat=Raw&safeSearch=Off&mkt=en-us`
        )
        setOptions({
          method: "GET",
          headers: {
            "X-BingApis-SDK": "true",
            "X-RapidAPI-Key": "723b0ce433msh85db6a452b6f6ebp111cf7jsn69ab9b7fe19b",
            "X-RapidAPI-Host": "bing-web-search1.p.rapidapi.com",
          },
        })
      },
      news: () => {
        setBaseUrl("https://bing-news-search1.p.rapidapi.com/news?textFormat=Raw&safeSearch=Off")
        setOptions({
          method: "GET",
          headers: {
            "X-BingApis-SDK": "true",
            "X-RapidAPI-Key": "723b0ce433msh85db6a452b6f6ebp111cf7jsn69ab9b7fe19b",
            "X-RapidAPI-Host": "bing-news-search1.p.rapidapi.com",
          },
        })
      },
      videos: () => {
        setBaseUrl("https://bing-video-search1.p.rapidapi.com/videos/search?q=beatles%20rain")
        setOptions({
          method: "GET",
          headers: {
            "X-RapidAPI-Key": "723b0ce433msh85db6a452b6f6ebp111cf7jsn69ab9b7fe19b",
            "X-RapidAPI-Host": "bing-video-search1.p.rapidapi.com",
          },
        })
      },
    }
  }

  const getResult = async (q, type) => {
    switch (type) {
      case "/search":
        base(q).link()
        break
      case "/images":
        base(q).images()
        break
      case "/news":
        base(q).news()
        break
      case "/videos":
        base(q).videos()
        break
      default:
        break
    }
    setIsLoading(true)

    const response = await fetch(baseUrl, options)

    const data = await response.json()
    console.log(data)

    setResult(data)
    setIsLoading(false)
  }

  return (
    <ResultContext.Provider value={{getResult, result, searchTerm, setSearchTerm, isLoading}}>
      {children}
    </ResultContext.Provider>
  )
}

export const useResultContext = () => {
  return useContext(ResultContext)
}
